'use client'

import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { 
  CreditCard, 
  Calendar, 
  DollarSign, 
  Download, 
  AlertCircle,
  CheckCircle,
  Loader2,
  Crown,
  Zap,
  Star,
  ArrowUpRight,
  RefreshCw,
  X
} from 'lucide-react'

interface User {
  id: string
  name: string
  email: string
  plan: 'basic' | 'pro' | 'enterprise'
  stripeCustomerId?: string
  subscriptionId?: string
  subscriptionStatus?: string
}

interface Invoice {
  id: string
  amount: number
  currency: string
  status: string
  created: number
  invoice_pdf?: string
  description?: string
}

interface PaymentMethod {
  id: string
  type: string
  card?: {
    brand: string
    last4: string
    exp_month: number
    exp_year: number
  }
}

export default function BillingPage() {
  const [user, setUser] = useState<User | null>(null)
  const [loading, setLoading] = useState(true)
  const [invoices, setInvoices] = useState<Invoice[]>([])
  const [paymentMethods, setPaymentMethods] = useState<PaymentMethod[]>([])
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [upgradeLoading, setUpgradeLoading] = useState(false)
  const [cancelLoading, setCancelLoading] = useState(false)

  useEffect(() => {
    loadBillingData()
  }, [])

  const loadBillingData = async () => {
    try {
      // Load user data
      const userResponse = await fetch('/api/auth/me')
      if (userResponse.ok) {
        const userData = await userResponse.json()
        setUser(userData.user)
      }

      // Load billing data
      const billingResponse = await fetch('/api/billing/data')
      if (billingResponse.ok) {
        const billingData = await billingResponse.json()
        setInvoices(billingData.invoices || [])
        setPaymentMethods(billingData.paymentMethods || [])
      }
    } catch (error) {
      console.error('Error loading billing data:', error)
      setError('Failed to load billing information')
    } finally {
      setLoading(false)
    }
  }

  const handleUpgrade = async (plan: 'pro' | 'enterprise') => {
    setUpgradeLoading(true)
    setError('')

    try {
      const response = await fetch('/api/stripe/create-checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ plan }),
      })

      const data = await response.json()

      if (response.ok) {
        window.location.href = data.checkoutUrl
      } else {
        setError(data.message || 'Failed to create checkout session')
      }
    } catch (error) {
      console.error('Upgrade error:', error)
      setError('An error occurred while processing your upgrade')
    } finally {
      setUpgradeLoading(false)
    }
  }

  const handleCancelSubscription = async () => {
    if (!confirm('Are you sure you want to cancel your subscription? You will lose access to premium features at the end of your billing period.')) {
      return
    }

    setCancelLoading(true)
    setError('')

    try {
      const response = await fetch('/api/billing/cancel-subscription', {
        method: 'POST',
      })

      const data = await response.json()

      if (response.ok) {
        setSuccess('Subscription cancelled successfully. You will retain access until the end of your billing period.')
        await loadBillingData()
      } else {
        setError(data.message || 'Failed to cancel subscription')
      }
    } catch (error) {
      console.error('Cancel subscription error:', error)
      setError('An error occurred while cancelling your subscription')
    } finally {
      setCancelLoading(false)
    }
  }

  const getPlanDetails = (plan: string) => {
    switch (plan) {
      case 'basic':
        return {
          name: 'Basic',
          price: 'FREE',
          color: 'text-gray-600 bg-gray-100',
          icon: <Star className="w-4 h-4" />
        }
      case 'pro':
        return {
          name: 'Pro',
          price: '$29/month',
          color: 'text-purple-600 bg-purple-100',
          icon: <Crown className="w-4 h-4" />
        }
      case 'enterprise':
        return {
          name: 'Enterprise',
          price: '$99/month',
          color: 'text-blue-600 bg-blue-100',
          icon: <Zap className="w-4 h-4" />
        }
      default:
        return {
          name: plan,
          price: 'Unknown',
          color: 'text-gray-600 bg-gray-100',
          icon: <Star className="w-4 h-4" />
        }
    }
  }

  const formatDate = (timestamp: number) => {
    return new Date(timestamp * 1000).toLocaleDateString()
  }

  const formatAmount = (amount: number, currency: string) => {
