'use client'

import { useState, useEffect } from 'react'
import dynamic from 'next/dynamic'
import { useRouter } from 'next/navigation'
import VisualEditor from '@/components/VisualEditor'

// Dynamically import Monaco Editor to avoid SSR issues
const MonacoEditor = dynamic(() => import('@monaco-editor/react'), { ssr: false })

interface FileItem {
  path: string
  content: string
  lastModified: Date
}

interface Deployment {
  id: string
  timestamp: Date
  status: 'pending' | 'building' | 'success' | 'failed'
  buildLogs?: string
  liveUrl?: string
  errorDetails?: string
}

export default function CodeEditorPage() {
  const router = useRouter()
  const [files, setFiles] = useState<FileItem[]>([])
  const [currentFile, setCurrentFile] = useState<FileItem | null>(null)
  const [code, setCode] = useState('')
  const [isSaving, setIsSaving] = useState(false)
  const [isDeploying, setIsDeploying] = useState(false)
  const [deployments, setDeployments] = useState<Deployment[]>([])
  const [showDeployments, setShowDeployments] = useState(false)
  const [editorMode, setEditorMode] = useState<'code' | 'visual'>('code')

  // Load files on mount
  useEffect(() => {
    loadFiles()
    loadDeployments()
  }, [])

  const loadFiles = async () => {
    try {
      const response = await fetch('/api/code-editor/files')
      if (response.ok) {
        const data = await response.json()
        setFiles(data.files)
        if (data.files.length > 0) {
          setCurrentFile(data.files[0])
          setCode(data.files[0].content)
        }
      }
    } catch (error) {
      console.error('Failed to load files:', error)
    }
  }

  const loadDeployments = async () => {
    try {
      const response = await fetch('/api/code-editor/deploy')
      if (response.ok) {
        const data = await response.json()
        setDeployments(data.deployments || [])
      }
    } catch (error) {
      console.error('Failed to load deployments:', error)
    }
  }

  const saveFile = async () => {
    if (!currentFile) return

    setIsSaving(true)
    try {
      const response = await fetch('/api/code-editor/files', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          filePath: currentFile.path,
          content: code
        })
      })

      if (response.ok) {
        // Update local file list
        setFiles(files.map(f => 
          f.path === currentFile.path 
            ? { ...f, content: code, lastModified: new Date() }
            : f
        ))
      }
    } catch (error) {
      console.error('Failed to save file:', error)
    } finally {
      setIsSaving(false)
    }
  }

  const deployToNetlify = async () => {
    setIsDeploying(true)
    try {
      const response = await fetch('/api/code-editor/deploy', {
        method: 'POST'
      })

      if (response.ok) {
        const data = await response.json()
        // Poll for deployment status
        pollDeploymentStatus(data.deploymentId)
      }
    } catch (error) {
      console.error('Failed to deploy:', error)
    } finally {
      setIsDeploying(false)
    }
  }

  const pollDeploymentStatus = async (deploymentId: string) => {
    const interval = setInterval(async () => {
      try {
        const response = await fetch(`/api/code-editor/deploy?deploymentId=${deploymentId}`)
        if (response.ok) {
          const data = await response.json()
          const deployment = data.deployment

          if (deployment.status === 'success' || deployment.status === 'failed') {
            clearInterval(interval)
            loadDeployments()
          }
        }
      } catch (error) {
        console.error('Failed to poll deployment status:', error)
        clearInterval(interval)
      }
    }, 3000)
  }

  return (
    <div className="h-screen flex flex-col bg-gray-900 text-white">
      {/* Header */}
