                  <p className="text-gray-700">{success}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {!canCreateWebsite() && (
          <Card className="bg-red-600/20 border-red-600/30 mb-8">
            <CardContent className="p-6">
              <div className="flex items-center space-x-3">
